import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Banner from '../../assests/images/banner-img1.jpeg';
import LeftArow from '../../assests/leftArow.png';
import RightArow from '../../assests/rightArow.png';
import Neckles from '../../assests/images/product5.jpeg';
import HandWatch from '../../assests/images/product8.jpeg';
import HandWatch1 from '../../assests/images/product9.jpeg';
import HandWatch2 from '../../assests/images/product2.jpeg';
import { Button } from 'antd';
import { Link } from 'react-router-dom';
import { Image } from 'antd';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
export default function ActionSlider() {
  const { t, i18n } = useTranslation();
  const [recentlyItems, setRecentlyItems] = useState([]);
  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: 'block', background: 'green' }}
        onClick={onClick}
      >
        <img src={LeftArow} alt="arrow_left" />
      </div>
    );
  }
  function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: 'block', background: 'red' }}
        onClick={onClick}
      >
        <img src={RightArow} alt="arrow_left" />
      </div>
    );
  }
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,

    slidesToShow: 5,
    slidesToScroll: 1,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
    responsive: [
      {
        breakpoint: 1440,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          centerMode: true,
          centerPadding: 30,
        },
      },

      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
          centerMode: true,
          centerPadding: 30,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          centerMode: true,
          centerPadding: 30,
        },
      },
    ],
  };
  useEffect(() => {
    axios
      .post(
        'https://us-central1-amibid-24a48.cloudfunctions.net/default-getRecentlySoldItems-default',
        {
          data: {
            // uid: localStorage.getItem('token'),
            // uid: '001',
            limit: 10,
            offset: 0,
          },
        },
      )
      .then(
        (response) => {
          const status = response.data.result.status;
          const data = response.data.result.data;
          if (status === 200) {
            setRecentlyItems(data);
          }
        },
        (error) => {
          console.log('error', error);
        },
      );
  }, []);
  return (
    <div>
      <div className="action_slider">
        <div className="silder_heading">
          <h2>{t('recently.title')}</h2>
          <Link>{t('recently-seemore')}</Link>
        </div>
        {recentlyItems && recentlyItems.length > 5 ? (
          <Slider {...settings}>
            {recentlyItems &&
              recentlyItems.map(
                (item) => (
                  // item.photos.map((photo) => (
                  <Link to={`/items/:${item.id}/auction`}>
                    <div className="slider_item">
                      <div className="silider_list">
                        <Image
                          src={item.photos[0]}
                          preview={false}
                          fallback="data:image/png;base64,"
                        />
                        <div className="slider_item_text">
                          <h2>{item.itemName}</h2>
                          <p className="price">£ {item.price}</p>
                          <div className="slider_item_des">
                            <div className="reaming_time">
                              <p>Time</p>
                            </div>
                            <div className="value">
                              <p>{t('recently-sold')}</p>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </Link>
                ),
                // )),
              )}
          </Slider>
        ) : (
          <div style={{ display: 'flex' }}>
            {recentlyItems.map((item) => (
              <div style={{ marginRight: '40px' }}>
                <Link to={`/items/:${item.id}/auction`} style={{ marginRight: '35px' }}>
                  <div className="slider_item">
                    <div className="silider_list">
                      <Image
                        src={item.photos[0]}
                        preview={false}
                        fallback="data:image/png;base64,"
                      />

                      <div className="slider_item_text">
                        <h2>{item.itemName}</h2>
                        <p className="price">£ {item.price}</p>
                        <div className="slider_item_des">
                          <div className="reaming_time">
                            <p>{t('recently-sold')}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </Link>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
