import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import LeftArow from '../../assests/leftArow.png';
import RightArow from '../../assests/rightArow.png';
import { Link } from 'react-router-dom';
import { Image } from 'antd';
import { useTranslation } from 'react-i18next';
import axios from 'axios';
import moment from 'moment';
import Countdown from './Countdown';
export default function ActionSlider() {
  const { t, i18n } = useTranslation();
  const [recomendedData, setRecomendedData] = useState([]);
  const [year, setYears] = useState('');
  const getDateYears = (secs) => {
    if (secs) {
      const data = new Date(secs * 1000);
      const date = moment(data).format();
      return date;
    }
  };
  useEffect(() => {
    axios
      .post('https://us-central1-amibid-24a48.cloudfunctions.net/default-getAuctionItems-default', {
        data: {
          limit: 10,
          offset: 0,
        },
      })
      .then(
        (response) => {
          const result = response.data.result;
          setRecomendedData(result.data);
          console.log(response);
        },
        (error) => {
          console.log('error', error);
        },
      );
  }, []);
  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: 'block', background: 'green' }}
        onClick={onClick}
      >
        <img src={LeftArow} alt="arrow_left" />
      </div>
    );
  }
  function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: 'block', background: 'red' }}
        onClick={onClick}
      >
        <img src={RightArow} alt="arrow_left" />
      </div>
    );
  }
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,

    slidesToShow: 5,
    slidesToScroll: 1,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
    responsive: [
      {
        breakpoint: 1440,
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          centerMode: true,
          centerPadding: 30,
        },
      },

      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
          centerMode: true,
          centerPadding: 30,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          centerMode: true,
          centerPadding: 30,
        },
      },
    ],
  };

  return (
    <div>
      <div className="action_slider">
        <div className="silder_heading">
          <h2>{t('recomended.title')}</h2>
          <Link>{t('see.more')}</Link>
        </div>
        {recomendedData && recomendedData.length > 5 ? (
          <Slider {...settings}>
            {recomendedData &&
              recomendedData.map(
                (item) => (
                  // item.photos.map((photo) => (
                  <Link to={`/items/:${item.id}/recommended`}>
                    <div className="slider_item">
                      <div className="silider_list">
                        <Image
                          src={item.photos[0]}
                          preview={false}
                          fallback="data:image/png;base64,"
                        />
                        <div className="slider_item_text">
                          <h2>{item.itemName}</h2>
                          <p className="price">£ {item.price}</p>
                          <div className="slider_item_des">
                            <div className="reaming_time">
                              <p>
                                <Countdown
                                  date={getDateYears(
                                    item.auctionEndTime && item.auctionEndTime._seconds,
                                  )}
                                />
                              </p>
                            </div>
                            <div className="value">
                              <p>{t('item.value')}</p>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </Link>
                ),
                // )),
              )}
          </Slider>
        ) : (
          <div style={{ display: 'flex' }}>
            {recomendedData.map((item) => (
              <div style={{ marginRight: '40px' }}>
                <Link to={`/items/:${item.id}/recommended`}>
                  <div className="slider_item">
                    <div className="silider_list">
                      <Image
                        src={item.photos[0]}
                        preview={false}
                        fallback="data:image/png;base64,"
                      />

                      <div className="slider_item_text">
                        <h2>{item.itemName}</h2>
                        <p className="price">£ {item.price}</p>
                        <div className="slider_item_des">
                          <div className="reaming_time">
                            <p>
                              <Countdown
                                date={getDateYears(
                                  item.auctionEndTime && item.auctionEndTime._seconds,
                                )}
                              />
                            </p>
                          </div>
                          <div className="value">
                            <p>{t('item.value')}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </Link>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
